import React, {useEffect, useState} from 'react'
// import BarChart from './components/BarChart'
import LineChart from './components/LineChart'
// import PieChart from './components/PieChart'

function App() {

  const [backendData, setBackendData] = useState([{}])
    const { setCurrentColor, setCurrentMode, currentMode, activeMenu, currentColor, themeSettings, setThemeSettings } = useStateContext();
  
    useEffect(() => {
      const currentThemeColor = localStorage.getItem('colorMode');
      const currentThemeMode = localStorage.getItem('themeMode');
      if (currentThemeColor && currentThemeMode) {
        setCurrentColor(currentThemeColor);
        setCurrentMode(currentThemeMode);
      }
    }, []);
  
    return (
      <div className={currentMode === 'Dark' ? 'dark' : ''}>
        <BrowserRouter>
          <div className="flex relative dark:bg-main-dark-bg">
            <div className="fixed right-4 bottom-4" style={{ zIndex: '1000' }}>
              <TooltipComponent
                content="Settings"
                position="Top"
              >
                <button
                  type="button"
                  onClick={() => setThemeSettings(true)}
                  style={{ background: currentColor, borderRadius: '50%' }}
                  className="text-3xl text-white p-3 hover:drop-shadow-xl hover:bg-light-gray"
                >
                  <FiSettings />
                </button>
  
              </TooltipComponent>
            </div>
            {activeMenu ? (
              <div className="w-72 fixed sidebar dark:bg-secondary-dark-bg bg-white ">
                <Sidebar />
              </div>
            ) : (
              <div className="w-0 dark:bg-secondary-dark-bg">
                <Sidebar />
              </div>
            )}
            <div
              className={
                activeMenu
                  ? 'dark:bg-main-dark-bg  bg-main-bg min-h-screen md:ml-72 w-full  '
                  : 'bg-main-bg dark:bg-main-dark-bg  w-full min-h-screen flex-2 '
              }
            >
              <div className="fixed md:static bg-main-bg dark:bg-main-dark-bg navbar w-full ">
                <Navbar />
              </div>
              <div>
                {themeSettings && (<ThemeSettings />)}
  
                <Routes>
                  {/* dashboard  */}
                  <Route path="/" element={(<Overall />)} />
                  <Route path="/Overall" element={(<Overall />)} />
  
                  {/* apps  */}
                  <Route path="/color-picker" element={<ColorPicker />} />
  
                  {/* charts  */}
                  <Route path="/Grades" element={<Line />} />
                  <Route path="/area" element={<Area />} />
                  <Route path="/Compare-Grades" element={<Bar />} />
                  <Route path="/Course-Categories" element={<Pie />} />
                  <Route path="/Submission" element={<ColorMapping />} />
                  <Route path="/Top-Resources" element={<Pyramid />} />
                  <Route path="/User-Activity" element={<Stacked />} />
  
                </Routes>
              </div>
              <Footer />
            </div>
          </div>
        </BrowserRouter>
      </div>
    );
  
  // useEffect(() => {
  //   fetch("/getGrades").then(
  //     response => response.json()
  //   ).then(
  //     data => {
  //       setBackendData(data)
  //     }
  //   )
  // }, [])

  return (
    <div>
        <LineChart/>
    </div>
  )
};

export default App
